import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Music, 
  Download, 
  Volume2, 
  Settings,
  FileText,
  AlertCircle,
  Loader
} from 'lucide-react';
import AudioUploader from './components/AudioUploader';
import PianoRoll from './components/PianoRoll';
import AudioPlayer from './components/AudioPlayer';
import SettingsPanel from './components/SettingsPanel';
import './App.css';

function App() {
  const [audioFile, setAudioFile] = useState(null);
  const [midiData, setMidiData] = useState(null);
  const [isConverting, setIsConverting] = useState(false);
  const [conversionProgress, setConversionProgress] = useState(0);
  const [error, setError] = useState(null);
  const [showSettings, setShowSettings] = useState(false);
  const [settings, setSettings] = useState({
    pitchBendRange: 2,
    driftThreshold: 0.5,
    quantizationStep: 0.5
  });

  const handleFileUpload = (file) => {
    setAudioFile(file);
    setMidiData(null);
    setError(null);
  };

  const handleConvert = async () => {
    if (!audioFile) return;

    setIsConverting(true);
    setConversionProgress(0);
    setError(null);

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('settings', JSON.stringify(settings));

    try {
      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setConversionProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return 90;
          }
          return prev + 10;
        });
      }, 500);

      const response = await fetch('/api/convert', {
        method: 'POST',
        body: formData,
      });

      clearInterval(progressInterval);

      if (!response.ok) {
        throw new Error('Conversion failed');
      }

      const result = await response.json();
      setMidiData(result);
      setConversionProgress(100);
      
      setTimeout(() => {
        setIsConverting(false);
        setConversionProgress(0);
      }, 1000);

    } catch (err) {
      setError(err.message);
      setIsConverting(false);
      setConversionProgress(0);
    }
  };

  const handleDownload = () => {
    if (!midiData) return;
    
    // Decode base64 MIDI content
    const binaryString = atob(midiData.midiContent);
    const bytes = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    
    const blob = new Blob([bytes], { type: 'audio/midi' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${audioFile.name.replace(/\.[^/.]+$/, '')}_converted.mid`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-12"
        >
          <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-4">
            Audio to MIDI Converter
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Transform your audio files into MIDI with beautiful piano roll visualization
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 max-w-7xl mx-auto">
          {/* Left Panel - Upload and Controls */}
          <motion.div 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="space-y-6"
          >
            {/* Upload Section */}
            <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
              <AudioUploader 
                onFileUpload={handleFileUpload}
                audioFile={audioFile}
              />
            </div>

            {/* Settings Panel */}
            <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                  <Settings className="w-5 h-5" />
                  Conversion Settings
                </h3>
                <button
                  onClick={() => setShowSettings(!showSettings)}
                  className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                >
                  {showSettings ? 'Hide' : 'Advanced'}
                </button>
              </div>
              
              <AnimatePresence>
                {showSettings && (
                  <motion.div
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: 'auto' }}
                    exit={{ opacity: 0, height: 0 }}
                    className="overflow-hidden"
                  >
                    <SettingsPanel 
                      settings={settings}
                      onSettingsChange={setSettings}
                    />
                  </motion.div>
                )}
              </AnimatePresence>

              <div className="mt-4 space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Pitch Bend Range:</span>
                  <span className="text-sm font-medium">{settings.pitchBendRange} semitones</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Drift Threshold:</span>
                  <span className="text-sm font-medium">{settings.driftThreshold}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Quantization Step:</span>
                  <span className="text-sm font-medium">{settings.quantizationStep}</span>
                </div>
              </div>
            </div>

            {/* Convert Button */}
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={handleConvert}
              disabled={!audioFile || isConverting}
              className={`w-full py-4 px-6 rounded-xl font-semibold text-lg transition-all duration-200 flex items-center justify-center gap-3 ${
                !audioFile || isConverting
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-gradient-to-r from-blue-600 to-purple-600 text-white hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl'
              }`}
            >
              {isConverting ? (
                <>
                  <Loader className="w-5 h-5 animate-spin" />
                  Converting<span className="loading-dots"></span>
                </>
              ) : (
                <>
                  <Music className="w-5 h-5" />
                  Convert to MIDI
                </>
              )}
            </motion.button>

            {/* Progress Bar */}
            {isConverting && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                className="bg-white rounded-xl p-4 border border-gray-100"
              >
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-gray-700">Progress</span>
                  <span className="text-sm text-gray-500">{conversionProgress}%</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <motion.div
                    className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full"
                    initial={{ width: 0 }}
                    animate={{ width: `${conversionProgress}%` }}
                    transition={{ duration: 0.3 }}
                  />
                </div>
              </motion.div>
            )}

            {/* Error Message */}
            <AnimatePresence>
              {error && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  className="bg-red-50 border border-red-200 rounded-xl p-4 flex items-center gap-3"
                >
                  <AlertCircle className="w-5 h-5 text-red-500" />
                  <span className="text-red-700">{error}</span>
                </motion.div>
              )}
            </AnimatePresence>

            {/* Download Button */}
            <AnimatePresence>
              {midiData && (
                <motion.button
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={handleDownload}
                  className="w-full py-4 px-6 bg-green-600 hover:bg-green-700 text-white rounded-xl font-semibold text-lg transition-all duration-200 flex items-center justify-center gap-3 shadow-lg hover:shadow-xl"
                >
                  <Download className="w-5 h-5" />
                  Download MIDI
                </motion.button>
              )}
            </AnimatePresence>
          </motion.div>

          {/* Right Panel - Piano Roll and Audio Player */}
          <motion.div 
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            className="space-y-6"
          >
            {/* Audio Player */}
            {audioFile && (
              <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
                  <Volume2 className="w-5 h-5" />
                  Audio Preview
                </h3>
                <AudioPlayer audioFile={audioFile} />
              </div>
            )}

            {/* Piano Roll */}
            <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
              <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <FileText className="w-5 h-5" />
                Piano Roll Visualization
              </h3>
              <PianoRoll midiData={midiData} isConverting={isConverting} />
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

export default App;
